void kernel_durbin(int n,
                   std::vector<double>& r,
                   std::vector<double>& y) {
    std::vector<double> z(n, 0.0);
    y[0] = -r[0];
    double beta = 1.0;
    double alpha = -r[0];
    for (int k = 1; k < n; ++k) {
        beta *= (1.0 - alpha * alpha);
        double s = 0.0;
        for (int i = 0; i < k; ++i) {
            s += r[k - 1 - i] * y[i];
        }
        alpha = -(r[k] + s) / beta;
        for (int i = 0; i < k; ++i) {
            y[i] += alpha * y[k - 1 - i];
        }
        y[k] = alpha;
    }
}
