#include <vector>
#include <omp.h>

void kernel_adi(int tsteps, int n,
        std::vector<std::vector<double>>& u,
        std::vector<std::vector<double>>& v,
        std::vector<std::vector<double>>& p,
        std::vector<std::vector<double>>& q) {
    double DX = 1.0 / n;
    double DY = 1.0 / n;
    double DT = 1.0 / tsteps;
    double B1 = 2.0;
    double B2 = 1.0;
    double mul1 = B1 * DT / (DX * DX);
    double mul2 = B2 * DT / (DY * DY);
    double a = -mul1 / 2.0;
    double b = 1.0 + mul1;
    double c = a;
    double d = -mul2 / 2.0;
    double e = 1.0 + mul2;
    double f = d;

    for (int t = 1; t <= tsteps; ++t) {
        #pragma omp parallel for
        for (int i = 1; i < n - 1; ++i) {
            v[0][i] = 1.0;
            p[i][0] = 0.0;
            q[i][0] = 1.0;
            for (int j = 1; j < n - 1; ++j) {
                double denom = a * p[i][j-1] + b;
                p[i][j] = -c / denom;
                double u_minus = u[j][i-1];
                double u_curr = u[j][i];
                double u_plus = u[j][i+1];
                double q_prev = q[i][j-1];
                double num = -d * u_minus + (1.0 + 2.0 * d) * u_curr - f * u_plus - a * q_prev;
                q[i][j] = num / denom;
            }
            v[n-1][i] = 1.0;
            for (int j = n-2; j >= 1; --j) {
                v[j][i] = p[i][j] * v[j+1][i] + q[i][j];
            }
        }

        #pragma omp parallel for
        for (int i = 1; i < n - 1; ++i) {
            u[i][0] = 1.0;
            p[i][0] = 0.0;
            q[i][0] = 1.0;
            for (int j = 1; j < n - 1; ++j) {
                double denom = d * p[i][j-1] + e;
                p[i][j] = -f / denom;
                double v_minus = v[i-1][j];
                double v_curr = v[i][j];
                double v_plus = v[i+1][j];
                double q_prev = q[i][j-1];
                double num = -a * v_minus + (1.0 + 2.0 * a) * v_curr - c * v_plus - d * q_prev;
                q[i][j] = num / denom;
            }
            u[i][n-1] = 1.0;
            for (int j = n-2; j >= 1; --j) {
                u[i][j] = p[i][j] * u[i][j+1] + q[i][j];
            }
        }
    }
}
