void kernel_nussinov(int n, std::vector<int>& seq, std::vector<std::vector<int>>& table) {
    for (int i = n - 1; i >= 0; --i) {
        for (int j = i + 1; j < n; ++j) {
            int best = 0;
            best = (table[i][j-1] > best) ? table[i][j-1] : best;
            best = (table[i+1][j] > best) ? table[i+1][j] : best;
