#include <vector>

void kernel_covariance(int m, int n,
                       std::vector<std::vector<double>>& data,
                       std::vector<std::vector<double>>& cov,
                       std::vector<double>& mean) {
    for (int j = 0; j < m; ++j) {
        double sum = 0.0;
        for (int i = 0; i < n; ++i) {
            sum += data[i][j];
        }
        mean[j] = sum / n;
    }

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            data[i][j] -= mean[j];
        }
    }

    double inv_n_minus_one = 1.0 / (n - 1.0);
    #pragma omp parallel for
    for (int k = 0; k < n; ++k) {
        double *row = &data[k][0];
        for (int i = 0; i < m; ++i) {
            double val = row[i];
            for (int j = i + 1; j < m; ++j) {
                cov[i][j] += val * row[j];
            }
        }
    }
    for (int i = 0; i < m; ++i) {
        for (int j = i + 1; j < m; ++j) {
            cov[i][j] *= inv_n_minus_one;
            cov[j][i] = cov[i][j];
        }
    }
}
