void kernel_gramschmidt(int m, int n,
                        std::vector<std::vector<double>>& A,
                        std::vector<std::vector<double>>& R,
                        std::vector<std::vector<double>>& Q) {
    for (int k = 0; k < n; ++k) {
        double norm = 0.0;
        for (int i = 0; i < m; ++i) {
            norm += A[i][k] * A[i][k];
        }
        R[k][k] = std::sqrt(norm);
        double inv_r = 1.0 / R[k][k];
        for (int i = 0; i < m; ++i) {
            Q[i][k] = A[i][k] * inv_r;
        }
        #pragma omp parallel for
        for (int j = k + 1; j < n; ++j) {
            double r = 0.0;
            for (int i = 0; i < m; ++i) {
                r += Q[i][k] * A[i][j];
            }
            R[k][j] = r;
            for (int i = 0; i < m; ++i) {
                A[i][j] -= r * Q[i][k];
            }
        }
    }
