#include <cmath>
#include <numeric>
#include <vector>

void kernel_correlation(int m, int n,
            std::vector<std::vector<double>>& data,
            std::vector<std::vector<double>>& corr,
            std::vector<double>& mean,
            std::vector<double>& stddev) {
    double eps = 0.1;

    #pragma omp parallel for
    for (int j = 0; j < m; j++) {
        double sum = 0.0;
        for (int i = 0; i < n; i++) {
            sum += data[i][j];
        }
        mean[j] = sum / n;
    }

    #pragma omp parallel for
    for (int j = 0; j < m; j++) {
        double sum = 0.0;
        for (int i = 0; i < n; i++) {
            double diff = data[i][j] - mean[j];
            sum += diff * diff;
        }
        stddev[j] = std::sqrt(sum / n);
        if (stddev[j] <= eps) {
            stddev[j] = 1.0;
        }
    }

    #pragma omp parallel for
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            data[i][j] -= mean[j];
            data[i][j] /= std::sqrt(n) * stddev[j];
        }
    }

    for (int i = 0; i < m - 1; i++) {
        corr[i][i] = 1.0;
        for (int j = i + 1; j < m; j++) {
            double sum = 0.0;
            #pragma omp parallel for reduction(+:sum)
            for (int k = 0; k < n; k++) {
                sum += data[k][i] * data[k][j];
            }
            corr[i][j] = sum;
            corr[j][i] = sum;
        }
    }

    corr[m-1][m-1] = 1.0;
}
