        for (int i = 0; i < m; i++) {
            nrm += A[i][k] * A[i][k];
        }
        R[k][k] = std::sqrt(nrm);
        for (int i = 0; i < m; i++) {
            Q[i][k] = A[i][k] / R[k][k];
