        #pragma omp parallel for
        for (int j = k + 1; j < n; j++) {
            R[k][j] = 0.0;
            for (int i = 0; i < m; i++) {
                R[k][j] += Q[i][k] * A[i][j];
            }
            for (int i = 0; i < m; i++) {
                A[i][j] = A[i][j] - Q[i][k] * R[k][j];
